import React from "react";
import axios from "axios";
import MailchimpSubscribe from "react-mailchimp-subscribe";
import "./_NavBar.scss";
import logo from "../img/logo.png";

export default class NavBar extends React.Component {
  state = {
    email: "",
  };

  onEmailChange(event) {
    this.setState({ email: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);

    axios({
      method: "POST",
      url: "https://pading.us8.list-manage.com/subscribe/post?u=d46cc3d9bd302db29080d2487&amp;id=c74670f1d3",
      data: this.state,
    }).then((response) => {
      if (response.data.status === "success") {
        alert("Message sent");
        this.resetForm();
      } else if (response.data.status === "fail") {
        alert("Message failed to send");
      }
    });
  }

  resetForm() {
    this.setState({ email: "" });
  }

  render() {
    return (
      <div className="navbar">
        {/* aaaaa<MailchimpSubscribe url={process.env.REACT_APP_MAILCHIMP_URL} />aaaaa */}
        <div className="navbar-brand">
          <a href="/">
            <img src={logo} alt="Pading find the perfect place to meet" />
          </a>
        </div>
        <div className="navbar-menu">
          <div className="navbar-social">
            <a href="http://google.fr">
              <i className="fab fa-facebook"></i>
            </a>
            <a href="http://google.fr">
              <i className="fab fa-instagram"></i>
            </a>
            <a href="http://google.fr">
              <i className="fab fa-linkedin"></i>
            </a>
          </div>

          <div className="email-form email-form-background">
            <form
              target="_blank"
              method="POST"
              onSubmit={this.handleSubmit.bind(this)}
            >
              <input
                name="EMAIL"
                placeholder="Email address"
                type="email"
                value={this.state.name}
                onChange={this.onEmailChange.bind(this)}
              />
              <button className="btn-blue" type="submit">
                Try this app
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
