import React from "react";
import axios from "axios";
import MailchimpSubscribe from "react-mailchimp-subscribe";
import "./_NavBar.scss";
import logo from "../img/logo.png";

export default class NavBar extends React.Component {
  state = {
    email: "",
    url: 'vgbhj'
  };

  onEmailChange(event) {
    this.setState({ email: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
    let axiosConfig = {
      headers: {
          'authorization': "Basic " + Buffer.from('randomstring:' + '61a2045f375990ad55eedeb4cddcfe39-us8').toString('base64'),
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      }
  };
    axios.post(
      `https://pading.us8.list-manage.com/subscribe/post?u=d46cc3d9bd302db29080d2487&amp;id=c74670f1d3`,
      axiosConfig
    ).then((response) => {
      console.log(response)
    }).catch((error) => {
      console.log(error)
    })
  }

  resetForm() {
    this.setState({ email: "" });
  }

  render() {
    return (
      <div>
        <h1>react-mailchimp-subscribe Demo</h1>

        <h2>Default Form</h2>
        <MailchimpSubscribe url={url} />
        <h2>Custom Form</h2>
        <MailchimpSubscribe
          url={url}
          render={({ subscribe, status, message }) => (
            <CustomForm
              status={status}
              message={message}
              onValidated={formData => subscribe(formData)}
            />
          )}
        />
      </div>
    )
  }
}
