import React from 'react';
import './_NavBar.scss';
import logo from '../img/logo.png';

export default class NavBar extends React.Component {
  state = {
    email: ''
  }

  render() {

    function onEmailChange(event) {
      this.setState({ email: event.target.value })
    }

    return (
      <div className="navbar">
        <div className="navbar-brand">
          <a href="/">
            <img src={logo} alt="Pading find the perfect place to meet" />
          </a>
        </div>
        <div className="navbar-menu">
          <div className="navbar-social">
            <a href="http://google.fr">
              <i className="fab fa-facebook"></i>
            </a>
            <a href="http://google.fr">
              <i className="fab fa-instagram"></i>
            </a>
            <a href="http://google.fr">
              <i className="fab fa-linkedin"></i>
            </a>
          </div>

          <div className="email-form email-form-background">
            <form
              target="_blank"
              action="https://spondonit.us12.list-manage.com/subscribe/post?u=1462626880ade1ac87bd9c93a&amp;id=92a4423d01"
              method="get"
            >
              <input name="EMAIL"  required="" type="email" value={this.state.email} onChange={this.onEmailChange}/>
              <button className="btn-blue" type="submit">Try this app</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
