import React from "react";
import axios from "axios";
import MailchimpSubscribe from "react-mailchimp-subscribe";
import "./_NavBar.scss";
import logo from "../img/logo.png";

export default class NavBar extends React.Component {
  state = {
    email: "",
    url: "vgbhj",
  };

  onEmailChange(event) {
    this.setState({ email: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
    let axiosConfig = {
      headers: {
        authorization:
          "Basic " +
          Buffer.from(
            "randomstring:" + "61a2045f375990ad55eedeb4cddcfe39-us8"
          ).toString("base64"),
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    axios
      .post(
        `https://pading.us8.list-manage.com/subscribe/post?u=d46cc3d9bd302db29080d2487&amp;id=c74670f1d3`,
        axiosConfig
      )
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  resetForm() {
    this.setState({ email: "" });
  }

  render() {
    return (
      <div className="navbar">
        <div className="navbar-brand">
          <a href="/">
            <img src={logo} alt="Pading find the perfect place to meet" />
          </a>
        </div>
        <div className="navbar-menu">
          <div className="navbar-social">
            <a href="http://google.fr">
              <i className="fab fa-facebook"></i>
            </a>
            <a href="http://google.fr">
              <i className="fab fa-instagram"></i>
            </a>
            <a href="http://google.fr">
              <i className="fab fa-linkedin"></i>
            </a>
          </div>
          q aadd
          <MailchimpSubscribe
            url={this.state.url}
            render={({ subscribe, status, message }) => (
              <div>
                <form onSubmitted={(formData) => subscribe(formData)} />
                {status === "sending" && (
                  <div style={{ color: "blue" }}>sending...</div>
                )}
                {status === "error" && (
                  <div
                    style={{ color: "red" }}
                    dangerouslySetInnerHTML={{ __html: message }}
                  />
                )}
                {status === "success" && (
                  <div style={{ color: "green" }}>Subscribed !</div>
                )}
              </div>
            )}
          />
          lllll
        </div>
      </div>
    );
  }
}
