import React from "react";
import axios from "axios";
import MailchimpSubscribe from "react-mailchimp-subscribe";
import "./_NavBar.scss";
import logo from "../img/logo.png";

const CustomForm = ({ status, message, onValidated }) => {
  let email, name;
  const submit = () =>
    email &&
    name &&
    email.value.indexOf("@") > -1 &&
    onValidated({
      EMAIL: email.value,
      NAME: name.value
    });

  return (
    <div
      style={{
        background: "#efefef",
        borderRadius: 2,
        padding: 10,
        display: "inline-block"
      }}
    >
      {status === "sending" && <div style={{ color: "blue" }}>sending...</div>}
      {status === "error" && (
        <div
          style={{ color: "red" }}
          dangerouslySetInnerHTML={{ __html: message }}
        />
      )}
      {status === "success" && (
        <div
          style={{ color: "green" }}
          dangerouslySetInnerHTML={{ __html: message }}
        />
      )}
      <input
        style={{ fontSize: "2em", padding: 5 }}
        ref={node => (name = node)}
        type="text"
        placeholder="Your name"
      />
      <br />
      <input
        style={{ fontSize: "2em", padding: 5 }}
        ref={node => (email = node)}
        type="email"
        placeholder="Your email"
      />
      <br />
      <button style={{ fontSize: "2em", padding: 5 }} onClick={submit}>
        Submit
      </button>
    </div>
  );
};
export default class NavBar extends React.Component {
  state = {
    email: "",
    url: 'vgbhj'
  };

  onEmailChange(event) {
    this.setState({ email: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
    let axiosConfig = {
      headers: {
          'authorization': "Basic " + Buffer.from('randomstring:' + '61a2045f375990ad55eedeb4cddcfe39-us8').toString('base64'),
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      }
  };

  }

  resetForm() {
    this.setState({ email: "" });
  }


  render() {
    const url =
      "https://pading.us8.list-manage.com/subscribe/post?u=d46cc3d9bd302db29080d2487&amp;id=c74670f1d3";
    return (
      <div>
        <h1>react-mailchimp-subscribe Demo</h1>

        <h2>Default Form</h2>
        <MailchimpSubscribe url={url} />
        <h2>Custom Form</h2>
        <MailchimpSubscribe
          url={url}
          render={({ subscribe, status, message }) => (
            <CustomForm
              status={status}
              message={message}
              onValidated={formData => subscribe(formData)}
            />
          )}
        />
      </div>
    )
  }
}
